package com.photon.phresco.framework.rest.api;

import javax.servlet.http.HttpServletRequest;
import javax.ws.rs.core.Response;

import org.junit.Assert;
import org.junit.Test;
import org.springframework.mock.web.MockHttpServletRequest;

import com.photon.phresco.exception.PhrescoException;

public class PdfServiceTest extends RestBaseTest {
	PdfService pdfService = new PdfService();
	
	@Test
	 public void displayGeneratePopUpWithNoPdf() {
		 MockHttpServletRequest request = new MockHttpServletRequest();
		 request.setParameter("appDirName", "TestProject");
		 request.setParameter("reportDataType", "detail");
		 HttpServletRequest httpServletRequest = (HttpServletRequest)request;
		 Response showGeneratePdfPopup = pdfService.showGeneratePdfPopup(appDirName, "unit", "", httpServletRequest);
		 Assert.assertEquals(200, showGeneratePdfPopup.getStatus());
	}
	 
	@Test
	public void generatePdf() throws PhrescoException {
		// to generate pdf in unit page  
		MockHttpServletRequest request = new MockHttpServletRequest();
		request.setParameter("appDirName", "TestProject");
		request.setParameter("reportDataType", "detail");
		request.setParameter("pdfName", "testcheck");
		request.setParameter("customerId", customerId);
		request.setParameter("projectId", "TestProject");
		request.setParameter("appId", "TestProject");
		request.setParameter("username", userId);
		request.setParameter("userId", userId);
		request.setParameter("fromPage", "unit");
		request.setParameter("reportType", "unit");
		request.setParameter("reportName", "unit");
		request.setParameter("testType", "unit");
		request.setParameter("appDirName", "TestProject");
		request.setParameter("logo", "");
		request.setParameter("technologyName", "java-webservice");
		HttpServletRequest httpServletRequest = (HttpServletRequest) request;
		Response printAsPdf = actionservice.printAsPdf(httpServletRequest);
		Assert.assertEquals(200, printAsPdf.getStatus());
		
		// to generate pdf in  project list 
		MockHttpServletRequest request1 = new MockHttpServletRequest();
		request1.setParameter("appDirName", "TestProject");
		request1.setParameter("reportDataType", "detail");
		request1.setParameter("pdfName", "testcheck");
		request1.setParameter("customerId", customerId);
		request1.setParameter("projectId", "TestProject");
		request1.setParameter("appId", "TestProject");
		request1.setParameter("username", userId);
		request1.setParameter("userId", userId);
		request1.setParameter("fromPage", "All");
		request1.setParameter("reportType", "unit");
		request1.setParameter("appDirName", "TestProject");
		request1.setParameter("reportName", "unit");
		request1.setParameter("testType", "unit");
		request1.setParameter("logo", "");
		request1.setParameter("technologyName", "java-webservice");
		HttpServletRequest httpServletRequest1 = (HttpServletRequest) request1;
		Response printAsPdf1 = actionservice.printAsPdf(httpServletRequest1);
		Assert.assertEquals(200, printAsPdf1.getStatus());
	}
	
	 @Test
	 public void fetchDownloadReport() {
		 Response unitdownloadReport = pdfService.downloadReport("unit", "testcheck_detail.pdf", appDirName, "");
		 Response componentdownloadReport = pdfService.downloadReport("component", "testcheck_detail.pdf", appDirName, "");
		 Response functionaldownloadReport = pdfService.downloadReport("functional", "testcheck_detail.pdf", appDirName, "");
		 Response loaddownloadReport = pdfService.downloadReport("load", "testcheck_detail.pdf", appDirName, "");
		 Response performancedownloadReport = pdfService.downloadReport("performance", "testcheck_detail.pdf", appDirName, "");
		 Response manualdownloadReport = pdfService.downloadReport("manual", "testcheck_detail.pdf", appDirName, "");
		 Assert.assertEquals(200, unitdownloadReport.getStatus());
		 Assert.assertEquals(200, componentdownloadReport.getStatus());
		 Assert.assertEquals(200, functionaldownloadReport.getStatus());
		 Assert.assertEquals(200, loaddownloadReport.getStatus());
		 Assert.assertEquals(200, performancedownloadReport.getStatus());
		 Assert.assertEquals(200, manualdownloadReport.getStatus());
	 }
	 
	 @Test
	 public void DownloadReportNotFound() {
		 Response downloadReport = pdfService.downloadReport("unit", "testcheck_detai.pdf", appDirName, "");
		 Assert.assertEquals(200, downloadReport.getStatus());
	 }
	 
	 @Test
	 public void displayGeneratePopUp() {
		 MockHttpServletRequest request = new MockHttpServletRequest();
		 request.setParameter("appDirName", "TestProject");
		 request.setParameter("reportDataType", "detail");
		 HttpServletRequest httpServletRequest = (HttpServletRequest)request;
		 
		 Response showGeneratePdfPopup = pdfService.showGeneratePdfPopup(appDirName, "unit", "", httpServletRequest);
		 Assert.assertEquals(200, showGeneratePdfPopup.getStatus());
		 
		 Response showGeneratePdfPopupAll = pdfService.showGeneratePdfPopup(appDirName, "All", "", httpServletRequest);
		 Assert.assertEquals(200, showGeneratePdfPopupAll.getStatus());
		 
		 Response showGeneratePdfPopupFail = pdfService.showGeneratePdfPopup("xx", "All", "", httpServletRequest);
		 Assert.assertEquals(200, showGeneratePdfPopupFail.getStatus());
	 }
	
	 @Test
	 public void deleteReports() {
		 Response deleteReport = pdfService.deleteReport("unit", "testcheck_detail.pdf", appDirName, "");
		 Assert.assertEquals(200, deleteReport.getStatus());
	 }
}
